--
-- Structure de la table `password_resets`
--

DROP TABLE IF EXISTS `password_resets`;
CREATE TABLE IF NOT EXISTS `password_resets` (
  `email` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `token` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `created_at` timestamp NOT NULL,
  KEY `password_resets_email_index` (`email`),
  KEY `password_resets_token_index` (`token`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

-- --------------------------------------------------------

--
-- Structure de la table `project`
--

DROP TABLE IF EXISTS `project`;
CREATE TABLE IF NOT EXISTS `project` (
  `id` int(255) NOT NULL AUTO_INCREMENT,
  `tittle` text COLLATE utf8_unicode_ci NOT NULL,
  `content` text COLLATE utf8_unicode_ci NOT NULL,
  `img` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `design_id` int(11) NOT NULL,
  `created_at` timestamp NOT NULL,
  `updated_at` timestamp NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=71 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

--
-- Contenu de la table `project`
--

INSERT INTO `project` (`id`, `tittle`, `content`, `img`, `design_id`, `created_at`, `updated_at`) VALUES
(70, 'Thiết kế văn phòng, hội trường công sở', '<p>Thiết kế VĂN PH&Ograve;NG, HỘI TRƯỜNG, PH&Ograve;NG HỌP HỘI NGHỊ, PH&Ograve;NG GIAO DỊCH</p>\r\n\r\n<p>Ph&iacute; thiết kế nội thất:&nbsp;<strong>130,000&nbsp;</strong>VNĐ/m2&nbsp;&nbsp;&nbsp;&nbsp;<strong>Gi&aacute; cũ:</strong>&nbsp;150,000&nbsp;VNĐ/m2</p>\r\n\r\n<p><strong>ViHome</strong>&nbsp;ch&uacute;ng t&ocirc;i l&agrave; đơn vị trực tiếp thiết kế,&nbsp;<a href="http://khonggianmo.vn/thi-cong-noi-that-van-phong/dich-vu/thi-cong-noi-that-van-phong/"><strong>THIẾT KẾ THI C&Ocirc;NG TRỌN G&Oacute;I NỘI THẤT VĂN PH&Ograve;NG</strong></a>.Với nhiều năm kinh nghiệm trong nghề, c&ocirc;ng ty đ&atilde; ho&agrave;n th&agrave;nh nhiều c&ocirc;ng tr&igrave;nh lớn, c&oacute; chất lượng cao tr&ecirc;n khắp cả nước.<a href="http://khonggianmo.vn/thi-cong-noi-that-van-phong/dich-vu/thi-cong-noi-that-van-phong/"><strong>THIẾT KẾ V&Agrave; THI C&Ocirc;NG NỘI THẤT VĂN PH&Ograve;NG&nbsp;&nbsp;</strong></a>l&agrave; một thế mạnh của Kh&ocirc;ng Gian Mở. C&aacute;c c&ocirc;ng tr&igrave;nh văn ph&ograve;ng được ch&uacute;ng t&ocirc;i thiết kế nhiều như:<em>thiết kế ph&ograve;ng họp, thiết kế ph&ograve;ng gi&aacute;m đốc, thiết kế văn ph&ograve;ng giao dịch, thiết kế ph&ograve;ng l&agrave;m việc, thiết kế quầy Ng&acirc;n H&agrave;ng, thiết kế showroom, thiết kế hội trường vv...</em></p>\r\n\r\n<p>Mỗi một văn ph&ograve;ng, showroom ch&uacute;ng t&ocirc;i thiết kế ra đều c&oacute; một phong c&aacute;ch rất ri&ecirc;ng. V&agrave; tạo cho chủ đầu tư sự h&agrave;i l&ograve;ng. Đ&aacute;p ứng được c&aacute;c ti&ecirc;u ch&iacute; như: &quot; Phong c&aacute;ch độc đ&aacute;o, thu h&uacute;t kh&aacute;ch h&agrave;ng, c&ocirc;ng năng sử thuận tiện, chi ph&iacute; thấp&rdquo;</p>\r\n\r\n<p><strong>Với ti&ecirc;u tr&iacute; thiết kế văn ph&ograve;ng l&agrave;:</strong></p>\r\n\r\n<p><strong><em>&ldquo; Sang trọng, Tiện dụng, tối ưu h&oacute;a kh&ocirc;ng gian, chi ph&iacute; thấp&rdquo;.</em></strong></p>\r\n\r\n<p><strong>Kh&ocirc;ng Gian Mở</strong>&nbsp;đ&atilde; ho&agrave;n th&agrave;nh nhiều&nbsp;<a href="http://www.khonggianmo.vn/thiet-ke-noi-that-showroom/dich-vu/thiet-ke-noi-that-showroom/">Văn ph&ograve;ng đẹp</a>, mang lại cho kh&aacute;ch h&agrave;ng sự tin tưởng khi sử dụng sản phẩm.</p>\r\n\r\n<p><strong>* G&Oacute;I HO&Agrave;N THIỆN VĂN PH&Ograve;NG GỒM:</strong></p>\r\n\r\n<p><em>-&nbsp;<a href="http://www.khonggianmo.vn/thiet-ke-noi-that-showroom/dich-vu/thiet-ke-noi-that-showroom/">Thiết kế nội thất văn ph&ograve;ng</a>, hội trường, văn ph&ograve;ng c&ocirc;ng sở &nbsp; (&nbsp;<a href="http://www.khonggianmo.vn/dich-vu/thiet-ke-noi-that/thiet-ke-noi-that">B&aacute;o gi&aacute; thiết kế</a>)</em></p>\r\n\r\n<p><em>-&nbsp;<a href="http://www.khonggianmo.vn/thiet-ke-noi-that-showroom/dich-vu/thiet-ke-noi-that-showroom/">Thiết kế ngoại thất văn ph&ograve;ng, showroom</a>&nbsp;( biển bảng)&nbsp;</em></p>\r\n\r\n<p><em>-&nbsp;<a href="http://www.khonggianmo.vn/thiet-ke-noi-that-showroom/dich-vu/thiet-ke-noi-that-showroom/">Thi c&ocirc;ng nội thất văn ph&ograve;ng</a>, tr&ecirc;n nhiều chất liệu:&nbsp;</em></p>\r\n\r\n<p>&nbsp;<em>&nbsp;&nbsp;&nbsp;&nbsp; Gỗ, k&iacute;nh, Mika, Inox, sắt, Alu, kết hợp với c&aacute;c phụ kiện kim kh&iacute;.</em></p>\r\n\r\n<p><em>- Thi c&ocirc;ng ngoại thất:&nbsp;<a href="http://www.khonggianmo.vn/thiet-ke-noi-that-showroom/dich-vu/thiet-ke-noi-that-showroom/">thi c&ocirc;ng biển bảng</a>, đ&egrave;n điện (&nbsp;<a href="http://www.khonggianmo.vn/dich-vu/thi-cong-noi-that/thi-cong-noi-that">B&aacute;o gi&aacute; thi c&ocirc;ng</a>)</em></p>\r\n\r\n<p><em>-&nbsp;<a href="http://www.khonggianmo.vn/thi-cong-noi-that-showroom/dich-vu/thi-cong-noi-that-showroom/">Cải tạo x&acirc;y dựng văn ph&ograve;ng</a>&nbsp;(&nbsp;<a href="http://www.khonggianmo.vn/dich-vu/thi-cong-noi-that/cai-tao-xay-dung">B&aacute;o gi&aacute; cải tạo x&acirc;y dựng</a>)</em></p>\r\n\r\n<p><img alt="" src="http://khonggianmo.vn/images/stories/vanphongshowroom/hoi%20truong%205.jpg" /></p>\r\n\r\n<p>Thiết kế hội trường c&ocirc;ng sở, hội trường kh&aacute;ch sạn</p>\r\n\r\n<p><img alt="" src="http://khonggianmo.vn/images/stories/vanphongshowroom/hoi%20truong%202.jpg" /></p>\r\n\r\n<p>Thiết kế hội trường c&ocirc;ng sở, hội trường kh&aacute;ch sạn</p>\r\n\r\n<p><img alt="" src="http://khonggianmo.vn/images/stories/vanphongshowroom/hoi%20truong%206.jpg" /></p>\r\n\r\n<p>Thiết kế hội trường c&ocirc;ng sở, hội trường kh&aacute;ch sạn</p>\r\n\r\n<p><img alt="" src="http://khonggianmo.vn/images/stories/vanphongshowroom/hoi%20truong%204.jpg" /></p>\r\n\r\n<p>Thiết kế ph&ograve;ng họp</p>\r\n\r\n<p><img alt="" src="http://khonggianmo.vn/images/stories/vanphongshowroom/hoi%20truong%201.jpg" /></p>\r\n\r\n<p>Thiết kế ph&ograve;ng họp,&nbsp;<a href="http://khonggianmo.vn/dich-vu/thi-cong-noi-that/thi-cong-noi-that">thi c&ocirc;ng nội thất c&ocirc;ng sở</a></p>\r\n\r\n<p>&nbsp; Với đội ngũ nh&acirc;n nh&acirc;n sự đ&ocirc;ng đảo v&agrave; nhiều kinh nghiệm. Vihome lu&ocirc;n ho&agrave;n th&agrave;nh tốt c&aacute;c c&ocirc;ng tr&igrave;nh m&agrave; kh&aacute;ch h&agrave;ng giao ph&oacute;. Kh&ocirc;ng Gian Mở khẳng định chữ &ldquo;t&iacute;n&rdquo; với kh&aacute;ch h&agrave;ng. &ldquo;Sản phẩm lỗi sẽ kh&ocirc;ng cho xuất xưởng&rdquo;.</p>\r\n', 'nhung-luu-y-khi-chon-mau-sac-cho-noi-that_s499.jpg', 1, '2016-09-22 18:55:30', '2016-09-22 18:55:30'),
(67, 'Biệt thự Đại lải Flamingo', '<p><strong>&nbsp; C&ocirc;ng tr&igrave;nh:&nbsp;</strong>&nbsp;<strong>THIẾT KẾ THI C&Ocirc;NG BIỆT THỰ NGHỈ DƯỠNG</strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Dự &aacute;n</strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : &nbsp; Thiết kế kiến tr&uacute;c,Thiết kế nội thất, thi c&ocirc;ng nội thất</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>Địa chỉ: &nbsp;&nbsp; KHU BIỆT THƯ ĐẠI LẢI FLAMINGO- VĨNH PH&Uacute;C</strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Quy m&ocirc; dự &aacute;n:</strong>&nbsp;&nbsp; S&gt; 550 m2, 2,5 tầng</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>Hạng mục:</strong>&nbsp;&nbsp; Thi c&ocirc;ng nội thất biệt thự, Cải tạo x&acirc;y dựng</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>Đơn vị thực hiện:&nbsp; Cty CP Kiến tr&uacute;c - Nội thất Kh&ocirc;ng Gian Mở</strong></p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Thiết kế KTS.Th.Sỹ. Nguyễn Hữu Chiến, KTS: Vũ Văn Hiệp</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp; Thời gian thi c&ocirc;ng:&nbsp; 01/10/2012 đến 23/11/2012</p>\r\n\r\n<p>Ng&agrave;y nay&nbsp;<a href="http://www.khonggianmo.vn/thiet-ke-noi-that/thiet-ke-noi-that-biet-thu/biet-thu-nghi-duong-dai-lai">Biệt thự nghỉ dưỡng</a>&nbsp;được xem l&agrave; căn nh&agrave; thứ hai của nhiều người d&acirc;n c&oacute; điều kiện về kinh tế. Một số địa chỉ l&yacute; tưởng ( khoảng c&aacute;ch gần với TT H&agrave; Nội) để c&aacute;c gia đỉnh chọn l&agrave;m nơi nghỉ dưỡng như: H&ograve;a B&igrave;nh, H&agrave; T&acirc;y cũ, Vĩnh Ph&uacute;c. Trong đ&oacute; Đại Lải Vĩnh Ph&uacute;c l&agrave; một trong những nơi l&yacute; tưởng đ&oacute;.</p>\r\n\r\n<p>Với biệt thự ở Đại Lải c&ocirc;ng ty KH&Ocirc;NG GIAN MỞ nhận được y&ecirc;u cầu của kh&aacute;ch h&agrave;ng l&agrave;: Thiết kế cho họ theo phong c&aacute;ch hiện đại, &aacute;nh s&aacute;ng tự nhi&ecirc;n phải được tận dụng tối đa. Đặc biệt thiết kế biệt thự n&agrave;y, phải thật gần gũi với thi&ecirc;n nhi&ecirc;n, c&aacute;c view phải tho&aacute;ng m&aacute;t, để ph&oacute;ng tầm mắt được xa nhất.</p>\r\n\r\n<p><img alt="Đại Lải Vĩnh Phúc - phối cảnh 3D kiến trúc mặt tiền biệt thự" src="http://www.khonggianmo.vn/images/stories/thietkebietthu/biet%20thu%20dai%20lai%2001.jpg" /></p>\r\n\r\n<p><a href="http://www.khonggianmo.vn/thiet-ke-noi-that/thiet-ke-noi-that-biet-thu/biet-thu-nghi-duong-dai-lai">Thiết kế biệt thự nghỉ dưỡng</a>&nbsp;- Đại Lải Vĩnh Ph&uacute;c - phối cảnh 3D kiến tr&uacute;c mặt tiền biệt thự</p>\r\n\r\n<p>Phần kiến tr&uacute;c được c&aacute;c KTS của Kh&ocirc;ng Gian Mở sử dụng chủ yếu bằng k&iacute;nh. Tạo cho view ngo&agrave;i của biệt thự sự tho&aacute;ng đ&atilde;ng v&agrave; gia chủ c&oacute; thể ph&oacute;ng tầm mắt ra xa nhất, với kh&ocirc;ng gian v&agrave; thi&ecirc;n nhi&ecirc;n b&ecirc;n ngo&agrave;i.</p>\r\n\r\n<p><img alt=" Đại Lải Vĩnh Phúc - phối cảnh 3D nội thất phòng khách biệt thự" src="http://www.khonggianmo.vn/images/stories/thietkebietthu/biet%20thu%20dai%20lai%206.jpg" /></p>\r\n\r\n<p><a href="http://www.khonggianmo.vn/thiet-ke-noi-that/thiet-ke-noi-that-biet-thu/biet-thu-nghi-duong-dai-lai">Thiết kế biệt thự nghỉ dưỡng</a>&nbsp;- Đại Lải Vĩnh Ph&uacute;c - phối cảnh 3D nội thất ph&ograve;ng kh&aacute;ch biệt thự</p>\r\n\r\n<p><img alt="Đại Lải Vĩnh Phúc - phối cảnh 3D nội thất phòng bếp" src="http://www.khonggianmo.vn/images/stories/thietkebietthu/biet%20thu%20dai%20lai%208.jpg" /></p>\r\n\r\n<p><a href="http://www.khonggianmo.vn/thiet-ke-noi-that/thiet-ke-noi-that-biet-thu/biet-thu-nghi-duong-dai-lai">Thiết kế biệt thự nghỉ dưỡng</a>&nbsp;- Đại Lải Vĩnh Ph&uacute;c - phối cảnh 3D nội thất ph&ograve;ng bếp</p>\r\n\r\n<p><img alt="Đại Lải Vĩnh Phúc - phối cảnh 3D nội thất phòng bếp-v2" src="http://www.khonggianmo.vn/images/stories/thietkebietthu/bt%20dai%20lai%201.jpg" /></p>\r\n\r\n<p><img alt="Đại Lải Vĩnh Phúc - phối cảnh 3D nội thất phòng khách biệt thự" src="http://www.khonggianmo.vn/images/stories/thietkebietthu/biet%20thu%20dai%20lai%204.jpg" /></p>\r\n\r\n<p><a href="http://www.khonggianmo.vn/thiet-ke-noi-that/thiet-ke-noi-that-biet-thu/biet-thu-nghi-duong-dai-lai">Thiết kế biệt thự nghỉ dưỡng</a>&nbsp;- Đại Lải Vĩnh Ph&uacute;c - phối cảnh 3D nội thất ph&ograve;ng kh&aacute;ch biệt thự</p>\r\n\r\n<p><img alt="Đại Lải Vĩnh Phúc - phối cảnh 3D nội thất phòng ngủ biệt thự" src="http://www.khonggianmo.vn/images/stories/thietkebietthu/biet%20thu%20dai%20lai%205.jpg" /></p>\r\n\r\n<p><a href="http://www.khonggianmo.vn/thiet-ke-noi-that/thiet-ke-noi-that-biet-thu/biet-thu-nghi-duong-dai-lai">Thiết kế biệt thự nghỉ dưỡng</a>&nbsp;- Đại Lải Vĩnh Ph&uacute;c - phối cảnh 3D nội thất ph&ograve;ng ngủ biệt thự</p>\r\n\r\n<p><img alt=" Đại Lải Vĩnh Phúc - phối cảnh 3D nội thất phòng ngủ biệt thự" src="http://www.khonggianmo.vn/images/stories/thietkebietthu/biet%20thu%20dai%20lai%209.jpg" /></p>\r\n\r\n<p><a href="http://www.khonggianmo.vn/thiet-ke-noi-that/thiet-ke-noi-that-biet-thu/biet-thu-nghi-duong-dai-lai">Thiết kế biệt thự nghỉ dưỡng</a>&nbsp;- Đại Lải Vĩnh Ph&uacute;c - phối cảnh 3D nội thất ph&ograve;ng ngủ biệt thự</p>\r\n\r\n<p><a href="http://www.khonggianmo.vn/tu-van/tu-van-xay-dung/xay-dung-biet-thu-nghi-duong"><img alt="Đội xây dựng phần thô - công ty Không Gian Mở đang thi công tại công trình biệt thự Đại Lải cao cấp Flamingo" src="http://www.khonggianmo.vn/images/stories/tuvan/tc15.jpg" /></a></p>\r\n\r\n<p>Đội x&acirc;y dựng phần th&ocirc; - c&ocirc;ng ty Kh&ocirc;ng Gian Mở đang thi c&ocirc;ng tại&nbsp;<a href="http://www.khonggianmo.vn/thiet-ke-kien-truc-nha-hang/dich-vu/thiet-ke-kien-truc-biet-thu/">c&ocirc;ng tr&igrave;nh biệt thự</a>&nbsp;Đại Lải cao cấp&nbsp;Flamingo</p>\r\n\r\n<p><img alt="Thi công hoàn thiện biệt thự - Đội thi công của Không Gian Mở đang trong giai đoạn hoàn thiện công trình biệt thự Đại Lải cao cấp Flamingo" src="http://www.khonggianmo.vn/images/stories/thietkebietthu/btdl%203.jpg" /></p>\r\n\r\n<p><a href="http://www.khonggianmo.vn/dich-vu/thi-cong-noi-that/thi-cong-noi-that">Thi c&ocirc;ng ho&agrave;n thiện biệt thự</a>&nbsp;- Đội thi c&ocirc;ng của Kh&ocirc;ng Gian Mở đang trong giai đoạn ho&agrave;n thiện c&ocirc;ng tr&igrave;nh biệt thự Đại Lải cao cấp&nbsp;Flamingo</p>\r\n\r\n<p><img alt="Đội xây dựng phần thô - công ty Không Gian Mở đang thi công tại công trình biệt thự Đại Lải cao cấp Flamingo" src="http://www.khonggianmo.vn/images/stories/tuvan/tc16.jpg" /></p>\r\n\r\n<p>Đội x&acirc;y dựng phần th&ocirc; - c&ocirc;ng ty Kh&ocirc;ng Gian Mở đang thi c&ocirc;ng tại c&ocirc;ng tr&igrave;nh biệt thự Đại Lải cao cấp&nbsp;Flamingo</p>\r\n\r\n<p><img alt="Đội thi công điện nước - công ty Không Gian Mở đang thi công tại công trình biệt thự Đại Lải cao cấp Flamingo" src="http://www.khonggianmo.vn/images/stories/tuvan/tc12.jpg" /></p>\r\n\r\n<p>Đội thi c&ocirc;ng điện nước - c&ocirc;ng ty Kh&ocirc;ng Gian Mở đang thi c&ocirc;ng tại c&ocirc;ng tr&igrave;nh biệt thự Đại Lải cao cấp&nbsp;Flamingo</p>\r\n\r\n<p><img alt="Đội xây thợ xây dựng của công ty Không Gian Mở luôn được trang bị bảo hộ lao động đầy dủ, các công nhân của công ty được đào tạo bài bản về quy trình làm việc, cũng như tác phong làm việc. " src="http://www.khonggianmo.vn/images/stories/tuvan/tc7.jpg" /></p>\r\n\r\n<p>Đội x&acirc;y thợ x&acirc;y dựng của c&ocirc;ng ty Kh&ocirc;ng Gian Mở lu&ocirc;n được trang bị bảo hộ lao động đầy dủ, c&aacute;c c&ocirc;ng nh&acirc;n của c&ocirc;ng ty được đ&agrave;o tạo b&agrave;i bản về quy tr&igrave;nh l&agrave;m việc, cũng như t&aacute;c phong l&agrave;m việc. B&ecirc;n cạnh đội c&ocirc;ng nh&acirc;n thi c&ocirc;ng x&acirc;y dựng cơ bản c&ocirc;ng ty lu&ocirc;n c&oacute; &iacute;t nhất một c&aacute;n bộ gi&aacute;m s&aacute;t kỹ thuật, để đ&ocirc;n đốc kiểm tra gi&aacute;m s&aacute;t. Ch&uacute;ng t&ocirc;i lu&ocirc;n ho&agrave;n th&agrave;nh c&ocirc;ng tr&igrave;nh một c&aacute;ch tốt nhất. Đem đến cho kh&aacute;ch h&agrave;ng sự tin tưởng. X&acirc;y dựng thương hiệu bền vững cho Kh&ocirc;ng Gian Mở</p>\r\n\r\n<p><img alt="Giám sát viên - công ty Không Gian Mở đang thi công tại công trình biệt thự Đại Lải cao cấp Flamingo" src="http://www.khonggianmo.vn/images/stories/tuvan/tc9.jpg" /></p>\r\n\r\n<p>Gi&aacute;m s&aacute;t vi&ecirc;n - c&ocirc;ng ty Kh&ocirc;ng Gian Mở đang thi c&ocirc;ng tại c&ocirc;ng tr&igrave;nh biệt thự Đại Lải cao cấp&nbsp;Flamingo</p>\r\n', 'noi-that-gia-re.jpg', 0, '2016-09-22 18:47:25', '2016-09-22 18:47:25'),
(68, 'THIẾT KẾ BIỆT THỰ VĂN PHÚ - HÀ ĐÔNG ', '<p><strong>Thiết kế biệt thự:</strong>&nbsp;<strong>150,000&nbsp;</strong>Đ/m2&nbsp;&nbsp;&nbsp;<strong>Gi&aacute; cũ:</strong>&nbsp;180,000&nbsp;Đ/m2</p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; C&ocirc;ng tr&igrave;nh:&nbsp;</strong>&nbsp;<strong>THIẾT KẾ THI C&Ocirc;NG BIỆT THỰ VĂN PH&Uacute;</strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Dự &aacute;n</strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : &nbsp; Thiết kế nội thất biệt thự, thi c&ocirc;ng nội thất</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>Địa chỉ: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; VĂN PH&Uacute; - H&Agrave; Đ&Ocirc;NG - H&Agrave; NỘI</strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Quy m&ocirc; dự &aacute;n:</strong>&nbsp;&nbsp; S&gt; 250 m2, 3,5 tầng</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>Hạng mục:</strong>&nbsp;&nbsp; Thiết kế nội thất biệt thự, ho&agrave;n thiện nội thất</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>Đơn vị thực hiện:&nbsp; Cty CP Kiến tr&uacute;c - Nội thất Kh&ocirc;ng Gian Mở</strong></p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Thiết kế KTS:&nbsp; Vũ Văn Cường,Th.Sỹ. Nguyễn Hữu Chiến&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp; Thời gian thực hiện:&nbsp; 25/8/2012 đến 25/09/2012</p>\r\n\r\n<p>Văn ph&uacute;, văn Kh&ecirc;, Dương Nội l&agrave; khu vực đang c&oacute; tốc độ ph&aacute;t triển mạnh về BDS, đặt biệt l&agrave; c&aacute;c dự &aacute;n về BDS Biệt thự. Với địa thế đẹp v&agrave; quy hoạch tổng thể tốt. Khu vực n&agrave;y đ&atilde; được nhiều nh&agrave; đầu tư BDS quan t&acirc;m với nhiều mục đ&iacute;ch. Như để đầu tư, để ở. C&aacute;c gia chủ ở đ&acirc;y khi chuyển về khu vực n&agrave;y ở phần lớn đều c&oacute; thi&ecirc;ng hướng: ho&agrave;n chỉnh cho căn biệt thự của m&igrave;nh được đẹp nhất c&oacute; thể.</p>\r\n\r\n<p>Với biệt thự ở của Anh Đạt ( chủ đầu tư căn biệt thự Văn Ph&uacute;) c&ocirc;ng ty KH&Ocirc;NG GIAN MỞ nhận được y&ecirc;u cầu của anh l&agrave;:&nbsp;<a href="http://www.khonggianmo.vn/thiet-ke-noi-that/thiet-ke-noi-that-biet-thu/thiet-ke-biet-thu-van-phu">Thiết kế biệt thự</a>cho họ theo phong c&aacute;ch hiện đại, &aacute;nh s&aacute;ng tự nhi&ecirc;n phải được tận dụng tối đa. C&aacute;c ph&ograve;ng chức năng phải rộng v&agrave; c&oacute; nhiều mảng Decor đẹp.</p>\r\n\r\n<p><a href="http://www.khonggianmo.vn/thiet-ke-noi-that/thiet-ke-noi-that-biet-thu/thiet-ke-biet-thu-van-phu"><img alt="Thiết kế biệt thự văn phú - phối cảnh 3D kiến trúc mặt tiền biệt thự - Phòng khách biệt thự" src="http://khonggianmo.vn/images/stories/thietkebietthu/bt%20van%20phu%2010.jpg" /></a></p>\r\n\r\n<p><a href="http://www.khonggianmo.vn/thiet-ke-noi-that/thiet-ke-noi-that-biet-thu/thiet-ke-biet-thu-van-phu">Thiết kế biệt thự văn ph&uacute;</a>&nbsp;- phối cảnh 3D kiến tr&uacute;c mặt tiền biệt thự - Ph&ograve;ng kh&aacute;ch biệt thự</p>\r\n\r\n<p><img alt="Thiết kế biệt thự văn phú - phối cảnh 3D kiến trúc mặt tiền biệt thự - Phòng khách biệt thự" src="http://khonggianmo.vn/images/stories/thietkebietthu/bt%20van%20phu%2018.jpg" /></p>\r\n\r\n<p><a href="http://www.khonggianmo.vn/thiet-ke-noi-that/thiet-ke-noi-that-biet-thu/thiet-ke-biet-thu-van-phu">Thiết kế biệt thự văn ph&uacute;</a>&nbsp;- phối cảnh 3D kiến tr&uacute;c mặt tiền biệt thự - Ph&ograve;ng kh&aacute;ch biệt thự</p>\r\n\r\n<p><img alt="Thiết kế biệt thự văn phú - phối cảnh 3D kiến trúc mặt tiền biệt thự - Phòng bếp" src="http://khonggianmo.vn/images/stories/thietkebietthu/bt%20van%20phu%2015.jpg" /></p>\r\n\r\n<p><a href="http://www.khonggianmo.vn/thiet-ke-noi-that/thiet-ke-noi-that-biet-thu/thiet-ke-biet-thu-van-phu">Thiết kế biệt thự văn ph&uacute;</a>&nbsp;- phối cảnh 3D kiến tr&uacute;c mặt tiền biệt thự - Ph&ograve;ng bếp</p>\r\n\r\n<p><img alt="Thiết kế biệt thự văn phú - phối cảnh 3D kiến trúc mặt tiền biệt thự - Phòng bếp-v2" src="http://khonggianmo.vn/images/stories/thietkebietthu/bt%20van%20phu%2016.jpg" /></p>\r\n\r\n<p><a href="http://www.khonggianmo.vn/thiet-ke-noi-that/thiet-ke-noi-that-biet-thu/thiet-ke-biet-thu-van-phu">Thiết kế biệt thự văn ph&uacute;</a>&nbsp;- phối cảnh 3D kiến tr&uacute;c mặt tiền biệt thự - Ph&ograve;ng bếp</p>\r\n\r\n<p><img alt="Thiết kế biệt thự văn phú - phối cảnh 3D kiến trúc mặt tiền biệt thự - Phòng ngủ" src="http://khonggianmo.vn/images/stories/thietkebietthu/bt%20van%20phu%202.jpg" /></p>\r\n\r\n<p><a href="http://www.khonggianmo.vn/thiet-ke-noi-that/thiet-ke-noi-that-biet-thu/thiet-ke-biet-thu-van-phu">Thiết kế biệt thự văn ph&uacute;</a>&nbsp;- phối cảnh 3D kiến tr&uacute;c mặt tiền biệt thự - Ph&ograve;ng ngủ</p>\r\n\r\n<p><img alt="Thiết kế biệt thự văn phú - phối cảnh 3D kiến trúc mặt tiền biệt thự - Phòng ngủ Master" src="http://khonggianmo.vn/images/stories/thietkebietthu/bt%20van%20phu%206.jpg" /></p>\r\n\r\n<p><a href="http://www.khonggianmo.vn/thiet-ke-noi-that/thiet-ke-noi-that-biet-thu/thiet-ke-biet-thu-van-phu">Thiết kế biệt thự văn ph&uacute;</a>&nbsp;- phối cảnh 3D kiến tr&uacute;c mặt tiền biệt thự - Ph&ograve;ng ngủ Master</p>\r\n\r\n<p><img alt="Thiết kế biệt thự văn phú - phối cảnh 3D kiến trúc mặt tiền biệt thự - Phòng ngủ Master-v2" src="http://khonggianmo.vn/images/stories/thietkebietthu/bt%20van%20phu%207.jpg" /></p>\r\n\r\n<p><img alt="Thiết kế biệt thự văn phú - phối cảnh 3D kiến trúc mặt tiền biệt thự - Phòng ngủ Master-v3" src="http://khonggianmo.vn/images/stories/thietkebietthu/bt%20van%20phu%208.jpg" /></p>\r\n\r\n<p><img alt="Thiết kế biệt thự văn phú - phối cảnh 3D kiến trúc mặt tiền biệt thự - Phòng ngủ 2" src="http://khonggianmo.vn/images/stories/thietkebietthu/bt%20van%20phu%201.jpg" /></p>\r\n\r\n<p><a href="http://www.khonggianmo.vn/thiet-ke-noi-that/thiet-ke-noi-that-biet-thu/thiet-ke-biet-thu-van-phu">Thiết kế biệt thự văn ph&uacute;</a>&nbsp;- phối cảnh 3D kiến tr&uacute;c mặt tiền biệt thự - Ph&ograve;ng ngủ 2</p>\r\n\r\n<p><img alt="Thiết kế biệt thự văn phú - phối cảnh 3D kiến trúc mặt tiền biệt thự - Phòng ngủ baby" src="http://khonggianmo.vn/images/stories/thietkebietthu/bt%20van%20phu%205.jpg" /></p>\r\n', 'thiet-ke-noi-that-chung-cu-90m2-2.jpg', 0, '2016-09-22 18:49:29', '2016-09-22 18:49:29'),
(69, 'Thiết kế văn phòng kết hợp Showroom giới thiệu sản phẩm', '<p><strong>&nbsp;Thiết kế VĂN PH&Ograve;NG SHOWROOM, THIẾT KẾ PH&Ograve;NG GIAO DỊCH</strong></p>\r\n\r\n<p>&nbsp;&nbsp; Ph&iacute; thiết kế nội thất:&nbsp;<strong>130,000&nbsp;</strong>Đ/m2&nbsp;&nbsp;&nbsp;&nbsp;<strong>Gi&aacute; cũ:</strong>&nbsp;150,000&nbsp;Đ/m2</p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp; DỰ &Aacute;N:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; SHOWROOM VĂN PH&Ograve;NG SƠN H&Agrave;N QUỐC</strong></p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;<strong>HẠNG MỤC</strong>: Thiết kế thi c&ocirc;ng nội thất, ngoại thất Showroom</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;<strong>ĐƠN VỊ TH:</strong>&nbsp; Cty Kiến tr&uacute;c v&agrave; Nội thất Kh&ocirc;ng Gian Mở</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;<strong>CHỦ TR&Igrave;:</strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; KST. L&ecirc; Mạnh Cường, Kts: Vũ Mạnh Cường</p>\r\n\r\n<p>Ng&agrave;y nay nhiều doanh nghiệp mới th&agrave;nh lập thường đưa ra cho m&igrave;nh một giải ph&aacute;p về văn ph&ograve;ng đ&oacute; l&agrave;:&nbsp; t&igrave;m một nh&agrave; d&acirc;n tr&ecirc;n mặt đường lớn, c&oacute; diện t&iacute;ch từ 30m2 đến 60m2 để l&agrave;m văn ph&ograve;ng,kết hợp với Showroom. Th&ocirc;ng thường c&aacute;c doanh nghiệp sẽ bố tr&iacute; tầng 1,2 l&agrave;m showroom giới thiệu sản phẩm. C&ograve;n tầng 3,4 l&agrave;m văn ph&ograve;ng l&agrave;m việc. C&aacute;ch l&agrave;m n&agrave;y sẽ tạo thuận lợi cho nhiều c&ocirc;ng ty c&oacute; kết hợp kinh doanh h&agrave;ng b&aacute;n sẵn như:&nbsp;<em><strong>Showroom kinh doanh Sơn, Showroom M&aacute;y t&iacute;nh, Showroom Mỹ phẩm, Showroom Thời trang, Showroom thiết bị d&acirc;n dụng, Showroom S&agrave;n gỗ, Văn ph&ograve;ng thiết kế nội thất vv...</strong></em></p>\r\n\r\n<p>Nhưng để kết hợp được 2 trong 1 đ&oacute; kh&ocirc;ng hề đơn giản. Bạn phải bố tr&iacute; văn ph&ograve;ng sao cho tinh tế v&agrave; khoa học. Nếu kh&ocirc;ng kh&aacute;ch h&agrave;ng đến Showroom sẽ kh&ocirc;ng c&oacute; thiện cảm.</p>\r\n\r\n<p>Việc thiết kế v&agrave; thi c&ocirc;ng văn ph&ograve;ng v&agrave; showroom kết hợp, kh&ocirc;ng phải ai cũng tự thực hiện được. Nếu bạn chưa c&oacute; kinh nghiệm l&agrave;m ho&agrave;n thiện, cải tạo showroom v&agrave; bạn tự đi thu&ecirc; c&aacute;c đội kh&aacute;c nhau v&agrave;o l&agrave;m cho bạn. Th&igrave; c&oacute; thể sẽ c&oacute; một số c&aacute;c vấn đề như sau:</p>\r\n\r\n<p><strong><em>- Nhiều đội kh&aacute;c nhau thi c&ocirc;ng việc sẽ bị chồng ch&eacute;o, dẫn đến tiến độ thi c&ocirc;ng chậm</em>.</strong></p>\r\n\r\n<p><strong>-&nbsp;<em>Khi sẩy ra sự cố th&igrave; kh&ocirc;ng ai đứng ra chịu tr&aacute;ch nhiệm</em></strong></p>\r\n\r\n<p><strong><em>- Sẽ kh&ocirc;ng c&oacute; người bảo h&agrave;nh, bảo dưỡng, v&igrave; c&aacute;c đội bạn thu&ecirc; phần lớn l&agrave; l&agrave;m tự do</em></strong></p>\r\n\r\n<p><strong><em>- Chất lượng sản phẩm sẽ kh&ocirc;ng đảm bảo v&igrave; bạn kh&ocirc;ng đủ thời gian để xem x&eacute;t kỹ c&aacute;c hạng mục.</em></strong></p>\r\n\r\n<p><strong><em>- Bạn phải bỏ nhiều c&ocirc;ng việc ch&iacute;nh của m&igrave;nh l&agrave; ( kinh doanh) sang l&agrave;m gi&aacute;m s&aacute;t thi c&ocirc;ng. Việc n&agrave;y c&ograve;n g&acirc;y nhiều thiệt hại hơn l&agrave; thu&ecirc; gi&aacute;m s&aacute;t.</em></strong></p>\r\n\r\n<p>Đ&oacute; l&agrave; một số vấn đề cơ bản m&agrave; nhiều người thực hiện ho&agrave;n thiện văn ph&ograve;ng vướng phải. Để giải quyết b&agrave;i to&aacute;n n&agrave;y tốt nhất bạn n&ecirc;n chọn một đơn vị&nbsp;<a href="http://www.khonggianmo.vn/dich-vu/thi-cong-noi-that/thi-cong-noi-that"><strong>THIẾT KẾ V&Agrave; THI C&Ocirc;NG NỘI THẤT</strong></a>, y&ecirc;u cầu họ l&agrave;m trọn g&oacute;i. D&ugrave; gi&aacute; bạn c&oacute; bỏ th&ecirc;m một ch&uacute;t chi ph&iacute;. Nhưng đổi lại bạn sẽ được đảm bảo tốt hơn.</p>\r\n\r\n<p>C&ocirc;ng ty&nbsp;<strong>KH&Ocirc;NG GIAN MỞ</strong>&nbsp;ch&uacute;ng t&ocirc;i l&agrave; đơn vị trực tiếp thiết kế,&nbsp;<a href="http://khonggianmo.vn/thi-cong-noi-that-van-phong/dich-vu/thi-cong-noi-that-van-phong/"><strong>THIẾT KẾ THI C&Ocirc;NG TRỌN G&Oacute;I NỘI THẤT VĂN PH&Ograve;NG</strong></a>.Với nhiều năm kinh nghiệm trong nghề, c&ocirc;ng ty đ&atilde; ho&agrave;n th&agrave;nh nhiều c&ocirc;ng tr&igrave;nh lớn, c&oacute; chất lượng cao tr&ecirc;n khắp cả nước.<a href="http://khonggianmo.vn/thi-cong-noi-that-van-phong/dich-vu/thi-cong-noi-that-van-phong/"><strong>THIẾT KẾ V&Agrave; THI C&Ocirc;NG NỘI THẤT VĂN PH&Ograve;NG&nbsp;&nbsp;</strong></a>l&agrave; một thế mạnh của Kh&ocirc;ng Gian Mở. C&aacute;c c&ocirc;ng tr&igrave;nh văn ph&ograve;ng được ch&uacute;ng t&ocirc;i thiết kế nhiều như:<em>thiết kế ph&ograve;ng họp, thiết kế ph&ograve;ng gi&aacute;m đốc, thiết kế văn ph&ograve;ng giao dịch, thiết kế ph&ograve;ng l&agrave;m việc, thiết kế quầy Ng&acirc;n H&agrave;ng, thiết kế showroom, thiết kế hội trường vv...</em></p>\r\n', 'noi-that-van-phong-dep.jpg', 1, '2016-09-22 18:53:16', '2016-09-22 18:53:16'),
(63, 'Thi công nội thất văn phòng Hai Bà Triệu', '<p><strong>C&ocirc;ng tr&igrave;nh:&nbsp;</strong><strong>THIẾT KẾ THI C&Ocirc;NG NỘI THẤT VĂN PH&Ograve;NG</strong></p>\r\n\r\n<p><strong>Dự &aacute;n</strong>&nbsp;:<strong>&nbsp;Thiết kế ho&agrave;n thiện Văn Ph&ograve;ng Hai B&agrave; Triệu</strong></p>\r\n\r\n<p><strong>Hạng mục:</strong>&nbsp;Thiết kế nội thất văn ph&ograve;ng, Thi c&ocirc;ng nội thất văn ph&ograve;ng, ho&agrave;n thiện c&aacute;c hạng mục điện nước, điện lạnh, điện mạng văn ph&ograve;ng, đồ gỗ văn ph&ograve;ng, biển bảng quảng c&aacute;o văn ph&ograve;ng l&agrave;m việc</p>\r\n\r\n<p><strong>Quy m&ocirc; dự &aacute;n:</strong>&nbsp;<strong>S = 250 m2</strong>, gồm c&aacute;c ph&ograve;ng ( Ph&ograve;ng Chủ Tịch, Ph&ograve;ng Gi&aacute;m Đốc, Ph&ograve;ng l&agrave;m việc nhan vi&ecirc;n, Ph&ograve;ng họp, ph&ograve;ng kh&aacute;ch</p>\r\n\r\n<p><strong>Đơn vị thực hiện: Cty CP kiến tr&uacute;c v&agrave; Nội thất KH&Ocirc;NG GIAN MỞ</strong></p>\r\n\r\n<p><strong>Thiết kế: Th.Sỹ/ KTS Nguyễn Hữu Chiến, KTS: Nguyễn Văn Trung,</strong></p>\r\n\r\n<p><strong>Thời Gian thực hiện thiết kế: 25/11/2013 đến 10/12/2013</strong></p>\r\n\r\n<p><a href="http://www.khonggianmo.vn/dich-vu/thi-cong-noi-that-van-phong/thi-cong-va-phong-hai-ba-trieu">Thiết kế nội thất vă ph&ograve;ng B&agrave; Triệu</a>&nbsp;- Ph&ograve;ng l&agrave;m việc của Chủ Tịch HĐQT c&ocirc;ng ty.</p>\r\n\r\n<p><img alt="thiet-ke-noi-that-van-phong-16" src="http://khonggianmo.vn/images/stories/TK_VanPhong/Vp_BaTrieu/tongthe.jpg" /></p>\r\n\r\n<p>Phối cảnh mặt bằng tổng thể văn ph&ograve;ng l&agrave;m việc, tại Hai B&agrave; Triệu -&nbsp;<strong>Thi c&ocirc;ng nội thất văn ph&ograve;ng</strong></p>\r\n\r\n<p><img alt="thiet-ke-noi-that-van-phong-3" src="http://khonggianmo.vn/images/stories/TK_VanPhong/Vp_BaTrieu/thiet-ke-noi-that-van-phong-3.jpg" /></p>\r\n\r\n<p><img alt="thiet-ke-noi-that-van-phong-4" src="http://khonggianmo.vn/images/stories/TK_VanPhong/Vp_BaTrieu/thiet-ke-noi-that-van-phong-4.jpg" /></p>\r\n\r\n<p><img alt="thiet-ke-noi-that-van-phong-5" src="http://khonggianmo.vn/images/stories/TK_VanPhong/Vp_BaTrieu/thiet-ke-noi-that-van-phong-5.jpg" /></p>\r\n\r\n<p><img alt="thiet-ke-noi-that-van-phong-1" src="http://khonggianmo.vn/images/stories/TK_VanPhong/Vp_BaTrieu/thiet-ke-noi-that-van-phong-01.jpg" /></p>\r\n\r\n<p><img alt="thiet-ke-noi-that-van-phong-7" src="http://khonggianmo.vn/images/stories/TK_VanPhong/Vp_BaTrieu/thiet-ke-noi-that-van-phong-7.jpg" /></p>\r\n\r\n<p>Thi c&ocirc;ng nội thất ph&ograve;ng họp - Phối cảnh 3D thiết kế nội thất văn ph&ograve;ng l&agrave;m việc tại Hai B&agrave; Trưng</p>\r\n\r\n<p><img alt="thiet-ke-noi-that-van-phong-8" src="http://khonggianmo.vn/images/stories/TK_VanPhong/Vp_BaTrieu/thiet-ke-noi-that-van-phong-8.jpg" /></p>\r\n\r\n<p><img alt="thiet-ke-noi-that-van-phong-9" src="http://khonggianmo.vn/images/stories/TK_VanPhong/Vp_BaTrieu/thiet-ke-noi-that-van-phong-9.jpg" /></p>\r\n\r\n<p><img alt="thiet-ke-noi-that-van-phong-10" src="http://khonggianmo.vn/images/stories/TK_VanPhong/Vp_BaTrieu/thiet-ke-noi-that-van-phong-10.jpg" /></p>\r\n\r\n<p><img alt="thiet-ke-noi-that-van-phong-11" src="http://khonggianmo.vn/images/stories/TK_VanPhong/Vp_BaTrieu/thiet-ke-noi-that-van-phong-11.jpg" /></p>\r\n\r\n<p><img alt="thiet-ke-noi-that-van-phong-12" src="http://khonggianmo.vn/images/stories/TK_VanPhong/Vp_BaTrieu/thiet-ke-noi-that-van-phong-13.jpg" /></p>\r\n\r\n<p><img alt="thiet-ke-noi-that-van-phong-15" src="http://khonggianmo.vn/images/stories/TK_VanPhong/Vp_BaTrieu/thiet-ke-noi-that-van-phong-15.jpg" /></p>\r\n\r\n<p><strong><a href="http://www.khonggianmo.vn/bao-gia-noi-that/bao-gia/bao-gia/">DỰ TO&Aacute;N HO&Agrave;N THIỆN THIỆN NỘI THẤT VĂN PH&Ograve;NG</a>&nbsp;(&nbsp;</strong>Thiết kế nội thất văn ph&ograve;ng<strong>)</strong></p>\r\n\r\n<table border="2" cellpadding="1" cellspacing="1">\r\n	<tbody>\r\n		<tr>\r\n			<td><strong>Mẫu chung cư</strong></td>\r\n			<td>\r\n			<p><strong>Thiết kế nội thất văn ph&ograve;ng - M&atilde;:&nbsp; TK&nbsp;<strong>- VPLV</strong></strong></p>\r\n			</td>\r\n			<td><strong>Chi ph&iacute; thiết kế ước t&iacute;nh</strong></td>\r\n			<td><strong>26.000.000&nbsp;</strong>VNĐ</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>K&iacute;ch thước</strong></td>\r\n			<td>&nbsp;<strong>s = 250m2</strong></td>\r\n			<td><strong>Chi ph&iacute; ho&agrave;n thiện nội thất chung cư (&nbsp;</strong><br />\r\n			chưa bao gồm cải tạo x&acirc;y dựng, nội thất)</td>\r\n			<td><strong>456.000.000&nbsp;</strong>VNĐ</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Y&ecirc;u cầu Thiết kế</strong></p>\r\n			</td>\r\n			<td>&nbsp;Thiết kế tổng thể văn ph&ograve;ng, Thiết kế c&aacute;c ph&ograve;ng chức năng, ph&ograve;ng Chủ tịch HĐQT, Ph&ograve;ng Gi&aacute;m Đốc, Ph&ograve;ng Nh&acirc;n vi&ecirc;n, Ph&ograve;ng họp</td>\r\n			<td><strong>Chi ph&iacute; cho trang thiết bị điện lạnh, điện chiếu s&aacute;ng, Thiết bị bếp</strong></td>\r\n			<td><strong>96.000.000&nbsp;</strong>VNĐ</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 'ags c4.jpg', 1, '2016-09-22 01:02:08', '2016-09-22 01:02:08'),
(65, 'THIẾT KẾ NỘI THẤT BIỆT THỰ VIỆT HƯNG', '<h3>Nội dung b&agrave;i viết</h3>\r\n\r\n<p><strong>Dự &aacute;n: THIẾT KẾ NỘI THẤT BIỆT THỰ VIỆT HƯNG</strong></p>\r\n\r\n<p><strong>Loại h&igrave;nh biệt thự</strong>: Biệt thự Đơn Lập</p>\r\n\r\n<p><strong>Quy m&ocirc; dự &aacute;n</strong>: Diện t&iacute;ch mặt s&agrave;n biệt thự 350m2, 3,5 tầng</p>\r\n\r\n<p><strong>Phong c&aacute;ch thiết kế biệt thự</strong>: Thiết kế theo phong c&aacute;ch T&acirc;n cổ điển. C&oacute; sự kết hợp giữa kiến tr&uacute;c hiện đại v&agrave; nội thất cổ điển. Một số đồ nội thất l&agrave; gỗ tự nhi&ecirc;n theo đồ giả cổ v&agrave; kết hợp với nội thất hiện đại gỗ c&ocirc;ng nghiệp.</p>\r\n\r\n<p><strong>Đơn vị thiết kế biệt thự:</strong>&nbsp;C&ocirc;ng ty CP Kiến tr&uacute;c v&agrave; Nội thất Kh&ocirc;ng Gian Mở</p>\r\n\r\n<p><strong>Th.Sỹ/ KTS</strong>: Nguyễn Hữu Chiến, KTS: Nguyễn Thế Anh, KTS: L&ecirc; Mạnh Cường</p>\r\n\r\n<p>Nhận được y&ecirc;u cầu của chủ nh&agrave; về việc&nbsp;<strong>Thiết kế kiến tr&uacute;c v&agrave;&nbsp;<a href="http://www.khonggianmo.vn/dich-vu/thiet-ke-noi-that-biet-thu/thiet-ke-biet-thu-viet-hung">Nội thất Biệt Thự</a></strong><a href="http://www.khonggianmo.vn/dich-vu/thiet-ke-noi-that-biet-thu/thiet-ke-biet-thu-viet-hung">&nbsp;<strong>Việt Hưng&nbsp;</strong></a>l&agrave;: Thiết kế Biệt thự theo phong c&aacute;ch b&aacute;n cổ điển. Với kiến tr&uacute;c b&ecirc;n ngo&agrave;i theo phong c&aacute;ch hiện đại, v&agrave; nội thất b&ecirc;n trong theo nhiều phong c&aacute;ch. Trong đ&oacute; ph&ograve;ng kh&aacute;ch được thiết kế theo phong c&aacute;ch cổ điển, với đồ nội thất giả cổ của Đồng Kỵ v&agrave; c&aacute;c ph&ograve;ng ngủ th&igrave; thiết kế theo phong c&aacute;ch hiện đại. C&aacute;c KTS của Kh&ocirc;ng Gian Mở đ&atilde; t&igrave;m ra c&aacute;c giải ph&aacute;p v&agrave; đưa ra c&aacute;c mẫu thiết kế cho chủ đầu tư.</p>\r\n\r\n<p><img alt="hiết kế kiến trúc biệt thự theo phong cách hiện đại" src="http://khonggianmo.vn/images/stories/TK_BIETTHU/nguyen/thietke_bietthu_vh%2001.jpg" /></p>\r\n\r\n<p>Phối cảnh kiến tr&uacute;c mặt tiền&nbsp;<em><strong><a href="http://www.khonggianmo.vn/dich-vu/thiet-ke-noi-that-biet-thu/thiet-ke-biet-thu-viet-hung">nh&agrave; biệt thự Việt Hưng</a></strong></em>&nbsp;- Thiết kế kiến tr&uacute;c biệt thự theo phong c&aacute;ch hiện đại</p>\r\n\r\n<p><img alt="" src="http://khonggianmo.vn/images/stories/TK_BIETTHU/nguyen/thietke_bietthu_vh%2002.jpg" /></p>\r\n\r\n<p><img alt="Thiết kế nội thất phòng khách Biệt thự" src="http://khonggianmo.vn/images/stories/TK_BIETTHU/nguyen/thietke_bietthu_vh%206.jpg" /></p>\r\n\r\n<p>Ph&ograve;ng kh&aacute;ch nh&agrave; Biệt thự Việt Hưng - Thiết kế nội thất ph&ograve;ng kh&aacute;ch Biệt thự theo phong c&aacute;ch T&acirc;n Cổ Điển - C&oacute; sự kết hợp của đồ nội thất l&agrave; đồ giả cổ ( Đồ gỗ Đồng kỵ) v&agrave; phần trang tr&iacute; theo phong c&aacute;ch hiện đại.</p>\r\n\r\n<p><img alt="Thiết kế nội thất phòng khách Biệt thự- v2" src="http://khonggianmo.vn/images/stories/TK_BIETTHU/nguyen/thietke_bietthu_vh%208.jpg" /></p>\r\n\r\n<p><img alt="Thiết kế nội thất phòng khách Biệt thự- v3" src="http://khonggianmo.vn/images/stories/TK_BIETTHU/nguyen/thietke_bietthu_vh%209.jpg" /></p>\r\n', '807850838962.jpg', 0, '2016-09-22 18:45:34', '2016-09-22 18:45:34'),
(66, 'Thiết kế chung cư Royal City', '<p><strong>C&ocirc;ng tr&igrave;nh:&nbsp;</strong><strong>THIẾT KẾ CHƯNG CƯ ROYAL CITY - VINCOM GROUP</strong></p>\r\n\r\n<p><strong>Dự &aacute;n</strong>&nbsp;: &nbsp;&nbsp;<strong>Thiết kế chung cư</strong>&nbsp;t&acirc;n cổ điển, Thi c&ocirc;ng nội thất chung cư</p>\r\n\r\n<p><strong>Hạng mục:</strong>&nbsp;Thi c&ocirc;ng cải tạo chung cư, ho&agrave;n thiện nội thất</p>\r\n\r\n<p><strong>Quy m&ocirc; dự &aacute;n:</strong>&nbsp;S &gt;150 m2, 03 P Ngủ , 01 P.Kh&aacute;ch, 01 P. Bếp</p>\r\n\r\n<p><strong>Đơn vị thực hiện: Cty CP kiến tr&uacute;c v&agrave; Nội thất KH&Ocirc;NG GIAN MỞ</strong></p>\r\n\r\n<p><strong>Thiết kế: KTS Nguyễn Văn Bi&ecirc;n, KTS: Nguyễn Hữu Chiến</strong></p>\r\n\r\n<p><strong>Thời Gian thực hiện thiết kế: 3/04/2013 đến 15/5/2013</strong></p>\r\n\r\n<p>Chung cư Royal City Tại H&agrave; Nội l&agrave; một trong những dự &aacute;n lớn của Vincom Group. Đ&acirc;y dự &aacute;n chung cư hạng cao cấp bậc nhất. Được thiết kế đồng bộ. C&aacute;c kh&aacute;ch h&agrave;ng khi lựa chọn mua tại đ&acirc;y đều c&oacute; những y&ecirc;u cầu rất khắt khe về thiết kế.</p>\r\n\r\n<p>Khi nhận được th&ocirc;ng tin của chủ nh&agrave; l&agrave;: Thiết kế nội thất chung cư Royal city theo phong c&aacute;ch t&acirc;n cổ điển, thi&ecirc;n về hiện đại. Nhưng phải đảm bảo sự sang trọng qu&yacute; ph&aacute;i, xứng tầm với dự &aacute;n v&agrave; căn hộ cao cấp. C&aacute;c KTS của Kh&ocirc;ng Gian Mở đ&atilde; bắt tay v&agrave; thiết kế. Với &yacute; tưởng l&agrave; sử dụng chất liệu gỗ tự nhi&ecirc;n, kết hợp với gỗ c&ocirc;ng nghiệp. Tạo ra nhưng mảng khối Decor rất qu&yacute; ph&aacute;i, mang đậm c&aacute; t&iacute;nh của chủ nh&agrave;.</p>\r\n\r\n<p><img alt="THIẾT KẾ CHƯNG CƯ ROYAL CITY -PHÒNG NGỦ" src="http://hoanthiennoithat.vn/images/ThietKeChungCu/chungcu_Royal/chung_cu_Royal_18.jpg" /></p>\r\n\r\n<p><img alt="THIẾT KẾ CHƯNG CƯ ROYAL CITY -PHÒNG NGỦ -V2" src="http://hoanthiennoithat.vn/images/ThietKeChungCu/chungcu_Royal/chung_cu_Royal_16.jpg" /></p>\r\n\r\n<p><img alt="" src="http://hoanthiennoithat.vn/images/ThietKeChungCu/chungcu_Royal/chung_cu_Royal_15.jpg" /></p>\r\n\r\n<p><img alt="" src="http://hoanthiennoithat.vn/images/ThietKeChungCu/chungcu_Royal/chung_cu_Royal_14.jpg" /></p>\r\n\r\n<p><img alt="" src="http://hoanthiennoithat.vn/images/ThietKeChungCu/chungcu_Royal/chung_cu_Royal_13.jpg" /></p>\r\n\r\n<p><img alt="" src="http://hoanthiennoithat.vn/images/ThietKeChungCu/chungcu_Royal/chung_cu_Royal_12.jpg" /></p>\r\n\r\n<p><img alt="" src="http://hoanthiennoithat.vn/images/ThietKeChungCu/chungcu_Royal/chung_cu_Royal_11.jpg" /></p>\r\n\r\n<p><img alt="Phòng Bếp" src="http://hoanthiennoithat.vn/images/ThietKeChungCu/chungcu_Royal/chung_cu_Royal_10.jpg" /></p>\r\n\r\n<p><img alt="Phòng ăn" src="http://hoanthiennoithat.vn/images/ThietKeChungCu/chungcu_Royal/chung_cu_Royal_9.jpg" /></p>\r\n\r\n<p><img alt="Phòng khách" src="http://hoanthiennoithat.vn/images/ThietKeChungCu/chungcu_Royal/chung_cu_Royal_8.jpg" /></p>\r\n\r\n<p><img alt="Phòng khách v2" src="http://hoanthiennoithat.vn/images/ThietKeChungCu/chungcu_Royal/chung_cu_Royal_7.jpg" /></p>\r\n\r\n<p><img alt="" src="http://hoanthiennoithat.vn/images/ThietKeChungCu/chungcu_Royal/chung_cu_Royal_6.jpg" /></p>\r\n\r\n<p><img alt="Phòng WC-v1" src="http://hoanthiennoithat.vn/images/ThietKeChungCu/chungcu_Royal/chung_cu_Royal_5.jpg" /></p>\r\n\r\n<p><img alt="Phòng WC- v2" src="http://hoanthiennoithat.vn/images/ThietKeChungCu/chungcu_Royal/chung_cu_Royal_4.jpg" /></p>\r\n\r\n<p><img alt="Phòng ngủ baby- v1" src="http://hoanthiennoithat.vn/images/ThietKeChungCu/chungcu_Royal/chung_cu_Royal_3.jpg" /></p>\r\n\r\n<p><img alt="Phòng ngủ baby- v2" src="http://hoanthiennoithat.vn/images/ThietKeChungCu/chungcu_Royal/chung_cu_Royal_2.jpg" /></p>\r\n\r\n<p><img alt="Phòng ngủ baby- v3" src="http://hoanthiennoithat.vn/images/ThietKeChungCu/chungcu_Royal/chung_cu_Royal_1.jpg" /></p>\r\n', 'noi-that-gia-re.jpg', 0, '2016-09-22 18:46:34', '2016-09-22 18:46:34');

